#     http://www.apache.org/licenses/LICENSE-2.0
#
# Unless required by applicable law or agreed to in writing, software
# distributed under the License is distributed on an "AS IS" BASIS,
# WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
# See the License for the specific language governing permissions and
# limitations under the License.
#solr.url=http://{{solr_host}}:{{solr_port}}/solr

#Service Logs and History colletion
logsearch.solr.zkhosts={{zookeeper_quorum}}{{solr_znode}}
logsearch.solr.collection.service.logs={{logsearch_collection_service_logs}}
logsearch.solr.collection.history=history

logsearch.service.logs.split.interval.mins={{logsearch_service_logs_split_interval_mins}}
logsearch.collection.service.logs.numshards={{logsearch_collection_service_logs_numshards}}
logsearch.collection.service.logs.replication.factor={{logsearch_collection_service_logs_replication_factor}}

logsearch.service.logs.fields={{logsearch_service_logs_fields}}

#Audit logs
logsearch.solr.audit.logs.zkhosts={{logsearch_solr_zk_quorum}}{{logsearch_solr_zk_znode}}
ogsearch.solr.collection.audit.logs={{solr_collection_audit_logs}}
logsearch.solr.audit.logs.url={{logsearch_solr_audit_logs_url}}

logsearch.audit.logs.split.interval.mins={{logsearch_audit_logs_split_interval_mins}}
logsearch.collection.audit.logs.numshards={{logsearch_collection_audit_logs_numshards}}
logsearch.collection.audit.logs.replication.factor={{logsearch_collection_audit_logs_replication_factor}}
{% if logsearch_solr_ssl_enabled %}
export LOGSEARCH_SSL="true"
export LOGSEARCH_KEYSTORE_LOCATION={{logsearch_keystore_location}}
export LOGSEARCH_KEYSTORE_PASSWORD={{logsearch_keystore_password}}
export LOGSEARCH_KEYSTORE_TYPE={{logsearch_keystore_type}}
export LOGSEARCH_TRUSTSTORE_LOCATION={{logsearch_truststore_location}}
export LOGSEARCH_TRUSTSTORE_PASSWORD={{logsearch_truststore_password}}
export LOGSEARCH_TRUSTSTORE_TYPE={{logsearch_truststore_type}}
{% endif %}

